mapper.select:
select l.title,l.date,l.log,l.hot from user u
inner join log l on u.id = l.userid
where l.userid = #{id}

select count(l.userid) from user u
inner join log l on u.id = l.userid
where l.userid = #{id}

select distinct u.card from user u
inner join log l on u.id = l.userid
where l.date = #{date}
